// JSON to default array format js
let movies = JSON.parse(localStorage.getItem("movies"));
if (movies === null) {
  movies = [
    {
      id: 1,
      title: "Spider Man",
      year: 2009,
      trailer: "https://www.youtube.com/embed/mZWsyUKwTbg",
      cover:
        "data:image/jpeg;base64,"
    },
    {
      id: 2,
      title: "Star Wars V",
      year: 2009,
      trailer: "https://www.youtube.com/embed/mZWsyUKwTbg",
      cover:
        "data:image/jpeg;base64,"
    }
  ];
  localStorage.setItem("movies", JSON.stringify(movies));
}

fillTable();

let form = document.getElementById("form");

form.addEventListener("submit", event => {
  const title = document.querySelector("#title").value;
  const year = document.querySelector("#year").value;
  const cover = document.querySelector("#cover").value;
  const trailer = document.querySelector("#trailer").value;

  movies.push({
    title: title,
    year: year,
    cover: cover,
    trailer: trailer
  });

  localStorage.setItem("movies", JSON.stringify(movies));

  console.table(movies);

  fillTable();

  form.reset();

  event.preventDefault();
});

function fillTable() {
  // get table tag
  // create the headers
  // put the movies data into the table

  let table = document.querySelector("#movies__table");

  table.innerHTML = `
                    <tr>
                        <th>Title</th>
                        <th>Year</th>
                        <th>Cover</th>
                        <th>Trailer</th>
                    </tr>
                    `;

  for (let movie of movies) {
    table.innerHTML += `
                    <tr>
                        <td>${movie.title}</td>
                        <td>${movie.year}</td>
                        <td>
                            <button onclick="showCover('${movie.cover}')">Open Cover</button>
                        </td>
                        <td><button onclick="showTrailer('${movie.trailer}')">Open Trailer</button></td>
                    </tr>
                    `;
  }
}

function showCover(cover) {
  let dialog = document.querySelector("#img__dialog");
  let cover__image = document.querySelector("#cover__image");

  cover__image.src = cover;

  dialog.showModal();
}

function showTrailer(trailer) {
  let dialog = document.querySelector("#trailer__dialog");

  // trailer = https://www.youtube.com/watch?v=QF97kL7KPHs

  let url = trailer.split("/");

  // get last array element
  let videoId = url[url.length - 1];

  if (videoId.indexOf("watch?v=") != -1) {
    videoId = videoId.replace("watch?v=", "");
  }

  let result = `https://www.youtube.com/embed/${videoId}`;

  let iframe = document.querySelector("#iframe");

  iframe.src = result;

  dialog.showModal();
}

// close dialog button
let close__dialog__button = document.querySelector("#close__dialog__button");
close__dialog__button.addEventListener("click", () => {
  let dialog = document.querySelector("#img__dialog");
  dialog.close();
});

// close dialog button2
let close__dialog__button2 = document.querySelector("#close__dialog__button2");
close__dialog__button2.addEventListener("click", () => {
  let dialog = document.querySelector("#trailer__dialog");
  dialog.close();
});
